export function ConfirmDisbursementModal(props: ConfirmDisbursementModalProps) {
  const dispatch = useDispatch();
  const {
    isVisible,
    selectedConsignments,
    transactionIdRequired,
    onClose,
    onSubmit,
    exportData,
  } = props;
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [isSubmitted, setIsSubmitted] = useState<boolean>(false);
  const [amount, setAmount] = useState<number>(0);
  const { confirmDisbursementsResponse } = useSelector((state: RootStateType) => {
    return {
      confirmDisbursementsResponse: state.confirmDisbursementsSlice,
    };
  });
  const handleDisbursement = () => {
    const payee_ids = selectedConsignments.map((rec) => rec.payee_id);
    const payload: ConfirmDisbursementPayloadType = {
      payment_amount: amount ?? 0,
      transaction_id: form.getFieldValue('transactionId'),
      payee_ids,
    };
    dispatch(confirmDisbursementsAsync(payload));
  };
  const handleClose = () => {
    dispatch(resetConfirmDisbursementData());
    setIsSubmitted(false);
    onSubmit();
    onClose();
  };
  const [form] = Form.useForm();
  useEffect(() => {
    if (confirmDisbursementsResponse.confirmDisbursementsReq === 'pending') {
      setIsLoading(true);
    }
    if (confirmDisbursementsResponse.confirmDisbursementsReq === 'fulfilled') {
      setIsLoading(false);
      setIsSubmitted(true);
      const cpIds = confirmDisbursementsResponse.consolidated_payment_ids;
      if (cpIds && cpIds.length) {
        exportData(cpIds, STATUS_TEXT_MAPPING[6]);
      }
    }
    if (confirmDisbursementsResponse.confirmDisbursementsReq === 'rejected') {
      setIsLoading(false);
      setIsSubmitted(true);
    }
  }, [confirmDisbursementsResponse]);
  useEffect(() => {
    const payableAmount = selectedConsignments.reduce((sum, rec) => {
      return (sum += Number(rec.payable_amount));
    }, 0);
    setAmount(payableAmount);
  }, [isVisible]);

  return (
    <Modal
      visible={isVisible}
      title={
        <ModalTitle
          isSubmitted={isSubmitted}
          confirmDisbursementsReq={confirmDisbursementsResponse.confirmDisbursementsReq}
        />
      }
      css={modalCss}
      closable={!isLoading}
      maskClosable={!isLoading}
      destroyOnClose
      centered
      footer={
        <Footer
          isSubmitted={isSubmitted}
          isLoading={isLoading}
          form={form}
          handleClose={handleClose}
        />
      }

