export const printConsignmentInvoice = async (record: any) => {
  try {
    const response: any = await consignmentRevampApi.printConsignmentInvoice(record.id);
    if (!response || response.isError) {
      throw new Error('could not download consignment Invoice');
    }
    if (response.pdfContent && response.pdfContent.template) {
      const template = {
        ...response.pdfContent.template,
        images: {
          redx_logo: `https://${url}/images/redx_logo.jpg`,
        },
      };
      generateAndOpenPdf(template);
    } else {
      message.error('Printing Consignment Invoice Failed');
    }
  } catch (error) {
    const typedError = error as Error;
    message.error(`Printing Consignment Invoice Failed: ${typedError.message}`);
  }
};

