function SalarySettle({ settlementCheck, queryObject, isFetching, onSuccess }) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { isSettled, isSnapshotGenerated } = settlementCheck || {};

  const handleClick = useCallback(async () => {
    try {
      setIsSubmitting(true);
      const paramsPrepared = _.omit(queryObject, ['limit', 'offset']);
      await salarySettleAPI.settleAgentsSalary(paramsPrepared);
      onSuccess(queryObject);
    } catch (error) {
      message.error(error?.message || 'Something went wrong.');
    }
    setIsSubmitting(false);
  }, [queryObject, onSuccess]);

  if (!settlementCheck) {
    return null;
  }

  if (!isSettled && isSnapshotGenerated) {
    return (
      <Button
        disabled={isFetching}
        onClick={handleClick}
        loading={isSubmitting}
        style={{ marginLeft: 20 }}
        type="primary">
        Settle
      </Button>
    );
  }

  return null;
}

