const AddRemarksModal = ({ visible, selectedEscalation, onOk, onCancel, moduleId }) => {
  const [remarks, setRemarks] = useState([]);
  const [remark, setRemark] = useState('');
  const [loading, setLoading] = useState(false);

  const getRemarks = async () => {
    try {
      const response = await EscalationApi.getRemarks({
        moduleId,
        issueTypeId: selectedEscalation.typeId,
      });
      setRemarks(response);
    } catch (err) {
      message.error(err.message);
    }
  };

  const submitSetRemarks = async () => {
    try {
      setLoading(true);
      if (!remark) {
        message.error('Set Remark First');
        return;
      }
      const remarks = remark;
      await EscalationApi.setRemarks({ remarks, issueId: selectedEscalation.id });
      setRemarks([]);
      setRemark('');
      setLoading(false);
      onOk();
    } catch (err) {
      message.error(err.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    if (!selectedEscalation) {
      return;
    }
    getRemarks();
  }, [selectedEscalation]);

  return (
    <Modal
      title={
        <div className="modal-title">
          Add Remarks to Issue for Parcel:{' '}
          <span>{selectedEscalation && selectedEscalation.parcelDetails.encodedId}</span>
        </div>
      }
      visible={visible}
      onOk={onOk}
      onCancel={onCancel}
      footer={null}
      centered>
      {selectedEscalation && Array.isArray(remarks) && (
        <div>
          {selectedEscalation.remarks && (
            <p>Current Remarks: {selectedEscalation.remarks}</p>
          )}
          <b>Set Remarks</b>
          <Select style={{ width: '100%' }} onChange={setRemark} value={remark}>
            {remarks.map((v, i) => (
              <Select.Option key={i} value={v.DESCRIPTION}>
                {v.DESCRIPTION}
              </Select.Option>
            ))}
          </Select>
          <Button
            style={{ 'margin-top': '10px' }}
            onClick={submitSetRemarks}
            loading={loading}>
            Submit
          </Button>
        </div>
      )}
    </Modal>
  );
};

