export function CommentTextAreaFormItem({ testId }: { testId: string }) {
  return (
    <Form.Item
      name="comment"
      rules={[
        { required: false },
        { max: 500, message: 'Comment cannot exceed 500 characters' },
        {
          validator: (_rule, value) => {
            if (!value) {
              return Promise.resolve();
            }

            if (value.replace(/\s/g, '').length === 0) {
              return Promise.reject('Invalid comment');
            }

            return Promise.resolve();
          },
        },
      ]}>
      <Input.TextArea
        data-testid={`${testId}`}
        rows={3}
        maxLength={500}
        showCount
        placeholder="please write additional comment here"
      />
    </Form.Item>
  );
}

