export default ({
  form,
  initialValues,
  onFilterChange,
  moduleCode,
  ownHubId,
  entityType,
  setEntityType,
}) => {
  const dispatch = useDispatch();
  const { hubs } = useSelector((state) => state.hub);

  const [filterShop, setFilterShop] = useState();
  const handleShopSelect = (selectedShop) => {
    setFilterShop(selectedShop);
    form.setFieldsValue({
      shopId: selectedShop?.value ? selectedShop.value : null,
    });
    form.submit();
  };

  const { issueTree, slaEscalationLevelDefs } = useSelector((state) => state.issueV3);
  const { userZones, userPermissions, user } = useSelector((state) => state.auth);

  const userRoles = user !== null ? user.Roles.map((role) => role.name) : null;

  const isPickupTeam =
    !userRoles.some((role) =>
      [
        'RedX Super Admin',
        'RedX Zone Escalations Manager',
        'RedX Hub Escalations Manager',
      ].includes(role),
    ) && userRoles.includes('RedX Pickup Team');

  const [hubsDisabled, setHubsDisabled] = useState(!isPickupTeam);
  const [hubList, setHubList] = useState([]);
  const [selectedZone, setSelectedZone] = useState([]);

  const [hubChangePermission, setHubChangePermission] = useState(false);

  const handleSetEscalationLevelOptions = useCallback(
    (slaBreachTypeCodeName) => {
      const options = slaEscalationLevelDefs.find(
        (item) => item.codeName === slaBreachTypeCodeName,
      )?.escalationLevelsWithSlaBreachKeys;
      const optionsMapped = (options || []).map((option) => ({
        ...option,
        slaBreachKeys: option.slaBreachKeys.join(','),
      }));
      form.setFieldsValue({
        [escalationLevelFieldName]: optionsMapped?.[0]?.slaBreachKeys,
      });
    },
    [slaEscalationLevelDefs, form],
  );

  const { rehydrated } = useSelector((state) => state._persist);

  useEffect(() => {
    if (!rehydrated) return;

    const hasHubChangePermission =
      userPermissions.filter(
        (item) =>
          item.id === 'redx:hubEscalationDashboard:changeHub' ||
          item.id === 'redx:adminPanel:changeAnything',
      ).length > 0;

    setHubChangePermission(hasHubChangePermission);

    dispatch(getIssueTreeAsync({ moduleCode, allowedAction: 'read', entityType }));
  }, [moduleCode, entityType]);

