export const CountCard = ({
  count,
  title,
  color,
  allowedAction,
  enabled,
  disabled,
}: CountCardPropType) => {
  const filterByCount = () => {
    if (allowedAction) {
      allowedAction();
    }
  };

  const filteredCellStyle = {
    backgroundColor: color ? color : 'white',
    opacity: enabled ? 1 : 0.8,
    cursor: disabled ? 'not-allowed' : 'pointer',
    borderColor: enabled ? '#000000' : '#dedede',
  };
  return (
    <div css={count_card_style} style={filteredCellStyle} onClick={filterByCount}>
      <p style={{ fontWeight: 'bold' }}>{count}</p>
      <p style={{ fontWeight: enabled ? 'bold' : 'normal' }}>{title}</p>
    </div>
  );
};

