export interface IssueTreePropsV2 {
  issueTree: Array<IssueType>;
  onChange?: (value: any) => void;
  value?: any;
  customStyle?: CSSProperties;
  antdTreeSelectProps?: TreeSelectProps<SelectValue>;
  onIssueSelect?: () => void;
}

const IssueTreeSelectV2 = (props: IssueTreePropsV2) => {
  const { issueTree, customStyle } = props;

  const handleIssueSelection: TreeSelectProps<SelectValue>['onChange'] = (value) => {
    if (props.onChange) {
      props.onChange(value);
    }

    if (props.onIssueSelect) {
      props.onIssueSelect();
    }
  };

  return (
    <TreeSelect<SelectValue>
      {...props.antdTreeSelectProps}
      placeholder="Issue type"
      style={customStyle ? customStyle : { width: 400 }}
      dropdownStyle={{ maxHeight: 700, overflow: 'auto' }}
      value={props.value}
      onChange={handleIssueSelection}
      showSearch
      allowClear
      filterTreeNode={(keyword, node) => {
        if (!node) {
          return false;
        }

        if (!node.title) {
          return false;
        }

        return (node.title as string).toLowerCase().includes(keyword.toLowerCase());
      }}>
      {renderChildren(issueTree)}
    </TreeSelect>
  );
};

export function ActionButtons(props: ActionButtonsProps) {
  if (props.availableActions.length === 0) {
    return null;
  }

  const renderActionButtons = (availableAction: AvailableActionType) => {
    const [, actionName] = availableAction.action.split(':');

    // not returning anything for *:comment action

    if (actionName === 'comment') {
      return null;
    }

    return (
      <Popconfirm
        key={availableAction.id}
        title="Are you sure?"
        disabled={props.isApiCallOngoing}
        onConfirm={() => props.performAction({ action: availableAction.action })}
        okText="Yes"
        cancelText="No">
        <Button
          size="small"
          type={ACTION_BUTTON_POP_CONFIRM_PROPS_MAP[actionName].buttonType}
          loading={props.isApiCallOngoing}
          disabled={props.isApiCallOngoing}>
          {ACTION_BUTTON_POP_CONFIRM_PROPS_MAP[actionName].buttonText}
        </Button>
      </Popconfirm>
    );
  };

  return (
    <div className="actions-section">
      {props.availableActions.map((availableAction) =>
        renderActionButtons(availableAction),
      )}
    </div>
  );
}

