export function CommentForm(props: CommentFormPropInterface) {
  const [commentForm] = Form.useForm();

  const commentBoxValidatior = (rule: RuleObject, value: any) => {
    if (!value) {
      return Promise.reject('please write a comment');
    }

    if (value.trim() === '') {
      return Promise.reject('please write a comment');
    }

    return Promise.resolve();
  };

  if (isEmpty(props.commentAction)) {
    return null;
  }

  return (
    <Form
      form={commentForm}
      initialValues={{ action: props.commentAction.action }}
      layout="vertical"
      onFinish={(values) =>
        props.performAction(values).then(() => commentForm.resetFields())
      }>
      <Form.Item name="action" hidden />
      <Form.Item
        label="Write additional comment"
        name="comment"
        rules={[
          {
            validator: commentBoxValidatior,
          },
        ]}>
        <Input.TextArea placeholder="Write Comment (Optional)" rows={4} />
      </Form.Item>
      <Button htmlType="submit" type="primary" style={{ float: 'right' }}>
        Add Comment
      </Button>
    </Form>
  );
}

