export function DetailedInformation(props: DetailedInformationPropsInterface) {
  const userPermissions = useSelector(
    (state: RootStateType) => state.auth.userPermissions as Array<{ id: string }>,
  );

  const hasPermissionToChangeIssuePriority =
    userPermissions.filter(
      (item) => item.id === 'redx:issueResolutionPanel:changeIssuePriority',
    ).length > 0;

  const isIRActionDisabled = useMemo(() => {
    const isDisableAsLostOrDamageIssue =
      LOST_DAMAGED_ISSUE_TYPE_CODE_NAMES.includes(props.issue?.typeDetails?.codeName) &&
      moment(props.issue.createdAt).isAfter(
        FINANCE_ISSUE_BYPASS_IR_FEATURE_DEPLOYMENT_DATE,
      );

    const isDisableAsLostOrDamageInTransitIssue =
      IN_TRANSIT_LOST_DAMAGED_ISSUE_TYPE_CODE_NAMES.includes(
        props.issue?.typeDetails?.codeName,
      ) && moment(props.issue.createdAt).isAfter(ITLD_FEATURE_DEPLOYMENT_DATE);

    return isDisableAsLostOrDamageIssue || isDisableAsLostOrDamageInTransitIssue;
  }, [props.issue.createdAt, props.issue.typeDetails?.codeName]);

  const getPendingWithValue = useCallback(() => {
    if (props.issue?.serial > 2 && props.issue?.bulkTransferId) {
      return '-';
    }

    const pendingWithMap = props.issue.pendingWith.map((v) => v.title);

    if (pendingWithMap.length === 0) {
      return 'None';
    }

    if (pendingWithMap.includes('Issue Resolution') && !isIRActionDisabled) {
      return 'Issue Resolution';
    }

    if (pendingWithMap.includes('In Transit Lost Damage') && isIRActionDisabled) {
      return 'In Transit Lost Damage';
    }

    return pendingWithMap.join(', ');
  }, [
    isIRActionDisabled,
    props.issue.bulkTransferId,
    props.issue.pendingWith,
    props.issue.serial,
  ]);

  if (props.isApiCallOngoing) {
    return (
      <div css={detailed_information_css}>
        <Spin size="small" />
      </div>
    );
  }

  if (isEmpty(props.issue)) {
    return null;
  }

  return (
    <div css={detailed_information_css}>
      <Descriptions bordered size="small" className="descriptions">
        {props.parcelMode === 'single' && (
          <Descriptions.Item span={3} label="Parcel ID">
            {props.issue.parcelDetails.encodedId}
          </Descriptions.Item>
        )}

        {props.parcelMode === 'bulk' && (
          <Descriptions.Item span={3} label="Bulk Transfer ID">
            {props.issue.bulkTransitMapping?.sourceBulkTransferId ||
              props.issue.bulkTransferId}
          </Descriptions.Item>
        )}

