      dispatch(getTruckCostAuditLogsAsync(selectedCostItem.id));
    }
  }, [dispatch, selectedCostItem]);

  useEffect(() => {
    if (router.query.limit && router.query.offset) {
      setItemPageNumber(router.query.offset / router.query.limit + 1);
    }
  }, [router.query.limit, router.query.offset]);

  const getVendorName = (vendorId) => {
    const vendor = vendorsList.find((v) => +v.id === vendorId);
    return vendor ? vendor.name : '';
  };

  return (
    <>
      <Table
        dataSource={truckCostList}
        rowKey={(row) => row.id.toString()}
        onChange={onPageChange}
        loading={getTruckCostsRequestsStatus === 'pending'}
        scroll={{ x: 2200, y: 400 }}

