const RedxCashModal = ({ state, onClose, onConfirm }: RedxCashModalProps) => {
  const [value, setValue] = useState<number>();
  const [isValid, setIsValid] = useState(true);

  useEffect(() => {
    if (state.isOpen) {
      setValue(state.data.redxCash);
      setIsValid(true);
    }
  }, [state.isOpen, state.data.redxCash]);

  const handleChange = (e: any) => {
    const cash = e.target.value;
    setValue(cash);
    setIsValid(cash !== undefined && cash >= 0);
  };

  const isValueChanged = Number(value) !== state.data.redxCash;
  const isConfirmDisabled = !isValid || !isValueChanged;

  return (
    <Modal
      title="Confirm Amount"
      visible={state.isOpen}
      onCancel={onClose}
      onOk={() => {
        if (value !== undefined) {
          onConfirm({
            parcelId: state.data.parcelId,
            payload: {
              cash: Number(value),
              reason: 'Update from 4pl payment verification',
              liftingPrice: 0,
            },
          });
        }
      }}
      okText="Confirm"
      centered
      okButtonProps={{ disabled: isConfirmDisabled }}
      data-testid="RedxCashModal">
      <Typography.Text strong data-testid="RedxCashModal_Lable">
        RedX Cash Amount
      </Typography.Text>

      <Input
        style={{ width: '100%', marginTop: 8 }}
        value={value}
        onChange={handleChange}
        placeholder="Enter RedX cash amount"
        data-testid="RedxCashModal_CashInput"
      />

      {!isValid && (
        <Typography.Text
          type="danger"
          style={{ marginTop: 8, display: 'block' }}
          data-testid="RedxCashModal_ValidationErrMessage">
          Amount must be a positive number
        </Typography.Text>
      )}
    </Modal>
  );
};

