export const NonBulkScan = () => {
  const router = useRouter();
  const { panel } = router.query;
  const cmhReceive = panel === 'new-receive';
  const cmhReturnReceive = panel === 'new-return-receive';
  const rmhReceive = panel === 'rmh-receive';
  const receiveFromHub = panel === 'receive-from-hub';
  const cmhRmhReceive = cmhReceive || rmhReceive;
  const transitFlow = panel === 'dispatch-transit';
  const returnTransitFlow = panel === 'return-dispatch-transit';

  return {
    cmhRmhReceive,
    rmhReceive,
    cmhReceive,
    receiveFromHub,
    cmhReturnReceive,
    transitFlow,
    returnTransitFlow,
  };
};

export const getSortingAction = async (moduleCode: string) => {
  const isReturn = moduleCode?.includes('return');
  const isAreaChange = moduleCode?.includes('area-change');
  let action = '';
  if (isReturn) {
    if (moduleCode?.includes('return-receive-from-isd')) {
      action = 'received-problematic-from-isd';
    }
    if (moduleCode?.includes('return-receive-from-cmh')) {
      action = 'received-problematic-from-cmh';
    }
    if (moduleCode?.includes('return-receive-from-rmh')) {
      action = 'received-problematic-from-rmh';
    }
    if (moduleCode?.includes('return-receive-from-subhub')) {
      action = 'received-problematic-from-subhub';
    }
  } else if (isAreaChange) {
    action = 'received-from-hub';
  } else {
    action = 'received-from-hub';
  }
  return {
    action,
  };
};

