export const printParcelStickers = async (parcelData) => {
  const requiredTemplate =
    parcelData[0].stickerData.deliveryType === 'Hub Delivery'
      ? 'redx-c2c-hub-zpl'
      : 'redx-c2c-home-zpl';
  const stickerDataList = parcelData.map((p) => p.stickerData);
  try {
    await printAPI.printParcelListWithPosPrinter(stickerDataList, requiredTemplate);
  } catch (error) {
    message.error(`Error printing parcel stickers`, error);
    console.log('error printing bulk parcels', error);
  }
};

export const fetchParcelsAndPrint = async (cnId, parcelIds) => {
  const payload = {
    trackingIds: parcelIds,
  };
  try {
    const parcelsResponse = await consignmentApi.getConsignmentParcelswithIds(
      cnId,
      payload,
    );
    if (parcelsResponse.isError) {
      throw new Error('could not get parcels');
    }
    await printParcelStickers(parcelsResponse.data);
    message.success(`Sent all instructions to printer!`);
  } catch (error) {
    console.log(`problem printing parcels:`, error);
    message.error(`Problem printing parcels: ${error.message}`);
  }
};

export const showBulkIdByBulkType = (bulks: SingleBulkType[]) => {
  const alteredRows = bulks.map((item) => {
    const bulkIdToShow = filterSecondaryIdIndex(item.secondaryBulkTransferId);
    const bulkIdToScan = fetchBulkIdToDisplayAndScan(item, bulkIdToShow);
    return {
      ...item,
      secondaryIdtoScan: bulkIdToScan,
    };
  });
  return alteredRows;
};

export const checkIfValidScanningId = (id: string) => {
  const regex = /^[a-zA-Z0-9_-]{10,}$/;
  return id.match(regex);
};

export const checkIfReattemptParcel = (id: string, parcels: LogisticsParcelsType[]) => {
  const scannedParcel = parcels.find((parcel) => parcel.trackingId === id);
  if (scannedParcel && labelledStatuses.includes(scannedParcel.status)) {
    return true;
  }
  return false;
};

export const printParcelWithPrinter = async (parcel?: LogisticsParcelsType) => {
  if (parcel) {
    if (!localStorage.posPrinterIp || !localStorage.posPrinterName) {
      return true;
    }
    try {
      await PrinterService.printParcelWithPosPrinter(parcel);
      PrinterService.logPrint(parcel);
    } catch (error) {
      message.error(error instanceof Error ? error.message : printerErrorMessage);
    }
  } else {
    message.error(noParcelDetailsErrorMessage);
  }
};

export const canPrintParcelModule = (moduleCode: string) => {
  if (
    moduleCode === 'rmh-area-change-receive-from-subhub' ||
    moduleCode === 'cmh-area-change-receive-from-isd'
  ) {
    return true;
  }
  return false;
};

export const fetchTrackingIds = (parcels: ParcelIdWithStatusType[] | string[]) => {
  const trackingIds: string[] = [];
  parcels.map((item: ParcelIdWithStatusType | string) => {
    if (typeof item === 'string') trackingIds.push(item);
    else trackingIds.push(item.trackingId);
  });
  return trackingIds;
};

export const showReceivedTimeOfParcel = (date: string) => {
