};

export const convertToTitleCase = (input: string) => {
  return input
    .split('_')
    .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
    .join(' ');
};

const useFetchOrUpdate = () => {
  const [loading, setLoading] = useState<boolean>(false);

  const makeAPICall = async (apiFunction: () => Promise<AxiosResponse<any>>) => {
    setLoading(true);
    try {
      const response = (await apiFunction()) as any;
      if (!response || response?.isError) {
        throw new Error(response?.message || logisticErrorMessage);
      }
      return response;
    } catch (err) {
      handleError(err);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  const handleError = (error: any) => {
    message.error(error.message || logisticErrorMessage);
  };

  return { makeAPICall, loading };
};

