      if (Number.isNaN(newAdjustmentAmount)) {
        message.error('Invalid adjustment amount');
        onError();
        return;
      }
      setFieldValue('adjustmentValues', newAdjustmentValues);
      setFieldValue('adjustmentAmount', newAdjustmentAmount);
      onSuccess();
    },
    [setFieldValue],
  );

  const handleAdjustmentAmountChange = useCallback(
    (event: ChangeEvent<HTMLInputElement>) => {
      handleNumericValueChange(setFieldValue, 'adjustmentAmount', event.target.value);
    },
