const ProgressTracker = () => {
  const timer = useRef();
  const router = useRouter();
  const shopId = parseInt(router.query.shopId, 10);

  const { invoices, progress, needConfirmation } = useSelector(
    (state) => state.pickupVisibilityBmPremise,
  );
  const dispatch = useDispatch();
  const noInvoices = invoices.length === 0;

  const avoidRender =
    !shopId || noInvoices || isNaN(progress.percent) || needConfirmation;

  useEffect(() => {
    if (avoidRender) return;
    else if (progress.percent === 100) return;

    timer.current = setTimeout(() => {
      dispatch(getInboundPickUpsAsync(shopId));
    }, 5 * 1000);

    return () => clearTimeout(timer.current);
  });

  if (avoidRender) return null;

  const successPercent = (progress.success * 100) / progress.total;
  return (
    <Row className="mt-20">
      <Col sm={{ span: 24 }} lg={{ span: 16, offset: 4 }} xxl={{ span: 12, offset: 6 }}>
        <Card className="d-flex">
          {progress.percent < 100 && (
            <span style={{ height: 14, marginRight: 10 }}>
              <SyncOutlined spin />
            </span>
          )}
          <Progress
            style={{ marginRight: 10 }}
            percent={progress.percent}
            success={{ percent: successPercent, strokeColor: '#52c41a' }}
            strokeColor="#ff4d4f"
            status="active"
            strokeLinecap="square"
          />
        </Card>
      </Col>
    </Row>
  );
};

