const Banner = ({ type, message }: BannerPropType) => {
  const getBannerCss = () => {
    if (type === 'warning') {
      return warning_banner_css;
    }
    if (type === 'info') {
      return info_banner_css;
    }
    if (type === 'error') {
      return error_banner_css;
    }
  };
  const getBannerIcon = () => {
    if (type === 'warning') {
      return <SvgCommonWarning />;
    }
    if (type === 'info') {
      return <SvgCommonInfo />;
    }
    if (type === 'error') {
      return <WarningOutlined style={{ color: '#F94949', fontSize: 22 }} />;
    }
  };
  return (
    <div css={getBannerCss}>
      {getBannerIcon()}
      <p style={{ marginLeft: 12 }}>{message}</p>
    </div>
  );
};

export const getCommissionTypes = (type: string) => {
  switch (type) {
    case 'isd':
      return ISDBasicCommissionType;
    case 'osd':
      return OSDBasicCommissionType;
    case 'sub':
      return SUBBasicCommissionType;
    default:
      return [];
  }
};

export const moveToCorrespondingAction = (
  actionType: string,
  campaign: BasicCampaignType | BonusCommissionType,
  router: NextRouter,
  type: CommissionType,
) => () => {
  const campaignId = campaign.campaignId;
  switch (actionType) {
    case 'viewCycle':
      router.push({
        pathname: `/salary-calculation/view-cycle`,
        query: { campaign: campaignId, type },
      });
      break;
    case 'editMetric':
      router.push({
        pathname: `/salary-calculation/edit-campaign-metric`,
        query: { campaign: campaignId, type },
      });
      break;
    case 'viewMetric':
      router.push({
        pathname: `/salary-calculation/view-campaign-metric`,
        query: { campaign: campaignId, type },
      });
      break;
  }
};

export const isValidFieldValue = (value: string) => {
  return value !== '';
};

export const validFieldName = (value: string) => {
  const regex = /^[a-zA-Z\d .\-_/]+$/;
  return value.match(regex);
};

export const getNextSlotValue = (initialSlotDate: Moment) => {
  const nextSlotDate = initialSlotDate.clone().add(1, 'day');
  const formattedDate = nextSlotDate.clone().add(1, 'month').date(1);
  if (initialSlotDate.date() >= 28) {
    return formattedDate;
  }
  return nextSlotDate;
};

export const fetchDateFromDateFormat = (values: Moment[]) => {
  return values.map((slot) => Number(moment(slot).format('DD')));
};

export const isDateArraySorted = (arr: Moment[]) => {
  for (let i = 0; i < arr.length - 1; i++) {
    if (arr[i] > arr[i + 1]) {
      return false;
    }
  }
  return true;
};

export const getCurrentMonth = () => {
  return moment().month();
};

export const getCurrentYear = () => {
  return moment().year();
};

export const getStartDate = () => {
  return moment().date(27).month(getCurrentMonth()).year(getCurrentYear());
};

export const constructDateForUpdatePrefill = (date: string) => {
  if (Number(date) === 27 || Number(date) === 28) {
    return moment().date(Number(date)).month(getCurrentMonth()).year(getCurrentYear());
  } else {
    return moment()
      .date(Number(date))
      .month(getCurrentMonth() + 1)
      .year(getCurrentYear());
  }
};

export const getEndDate = () => {
  return moment()
    .date(26)
    .month(getCurrentMonth() + 1)
    .year(getCurrentYear());
};

export const formatDateWithEmptyCheck = (
  date: Date | string,
  format = 'dddd, MMMM Do YYYY, h:mm A',
) => {
  if (date === '') {
    return '-';
  }
  return moment(date).format(format);
};

export const convertHubsValueToList = (hubIds: string) => {
  return hubIds.split(',').map((item) => +item.trim());
};

export const stringTypeValue = (value: number | string) => {
  return value.toString();
};

export const isValidMetricValue = (value: string) => {
  return value !== '' && Number(value) >= 0 && Number.isInteger(Number(value));
};

export const genericCampaignInfoBannerMessage = (
  data: BasicCampaignType | BonusCommissionType,
  action: string,
) => {
  if (data) {
    if (isValidFieldValue(data.endDate)) {
      return `You are ${action}ing commission structure from ${formatDateWithEmptyCheck(
        data.startDate,
        salaryDataFormat,
      )} to ${formatDateWithEmptyCheck(data.endDate, salaryDataFormat)}`;
    } else {
      return `You are ${action}ing commission structure for ${formatDateWithEmptyCheck(
        data.startDate,
        salaryDataFormat,
      )}`;
    }
  } else return '';
};

export const getValueForBasicAttempt = (attempts: string) => {
  return {
    is_enabled: true,
    config: {
      incentive: stringTypeValue(attempts),
    },
  };
};

