    if (details?.deductionBreakdown) {
      return details.deductionBreakdown.reduce(
        (acc, item) => acc + item.deductionAmount,
        0,
      );
    }
    return 0;
  };

  const getTotalRecovery = () => {
    if (details?.recoveryBreakdown) {
      return details.recoveryBreakdown.reduce(
