const ViewCampaignMetric = () => {
  const router = useRouter();
  const dispatch = useDispatch();
  const { basicCommissionData, bonusCommissionData } = useSelector(
    (state: RootStateType) => state.salaryHrReducers.commissionReducer,
  );
  useEffect(() => {
    getCommissionDetails();
  }, []);

  const commissionType = router.query.type as string;
  const campaignId = router.query.campaign as string;
  const isBasicCommissionType = commissionType === 'basic';

  const getCommissionDetails = () => {
    const params = {
      commission_type: commissionType,
      campaign_id: +campaignId,
    };
    if (isBasicCommissionType) {
      dispatch(getBasicCommissionAsync(params));
    } else {
      dispatch(getBonusCommissionAsync(params));
    }
  };

  const moveToListPage = () => {
    router.replace(`/salary-calculation/${commissionType}-commission`);
  };

  const viewCampaignMetricBannerInstance = {
    bannerMessage: genericCampaignInfoBannerMessage(
      isBasicCommissionType ? basicCommissionData[0] : bonusCommissionData[0],
      'view',
    ),
    bannerType: 'info',
  };

  return (
    <ShopUpLayout hideFooter>
      <CommissionHeader
        title={isBasicCommissionType ? 'View metric' : 'View details'}
        goBack={moveToListPage}
        bannerInstance={
          isBasicCommissionType ? viewCampaignMetricBannerInstance : undefined
        }
      />
      {isBasicCommissionType ? (
        <BasicCommissionView commissionData={basicCommissionData[0]?.kpis} />
      ) : (
        <BonusCommissionView commissionData={bonusCommissionData[0]?.kpis} />
      )}
    </ShopUpLayout>
  );
};

const BasicCommissionViewCycle = () => {
  const [isSettleModalVisible, openSettleModal] = useState(false);
  const [settlementDetail, setSettlementDetail] = useState(initialCycleItemDetail);
  const [isUpdateCycleModalVisible, openUpdateCycleModal] = useState(false);
  const [isIframeUrl, openIframeUrl] = useState(false);
  const {
    cycleData,
    settleCycleLoading,
    iframeUrl,
    iframeUrlLoading,
    cycleConfigData,
  } = useSelector((state: RootStateType) => state.salaryHrReducers.salaryReducer);
  const dispatch = useDispatch();
  const router = useRouter();
  const { type: typeQuery, campaign: campaignQuery } = router.query;
  const type = typeQuery as string;
  const campaign = campaignQuery as string;
  const { cycle, starts_at, ends_at, id: cycleId } = settlementDetail;

  const initialCycleConfig = [getStartDate(), getStartDate().clone().add(1, 'day')];
  const updateCycleFormInitialValues: CycleConfigFormInitialValueType = {
    slots: initialCycleConfig,
  };

  useEffect(() => {
    if (type === 'basic') {
      message.info('The cycle configs should be updated only in the last slot');
    }
    getCampaignCycleList();
  }, []);

  useEffect(() => {
    if (cycleConfigData.length > 0) {
      updateCycleForm.setValues({
        slots: cycleConfigData.map((item: CycleConfigType) =>
          constructDateForUpdatePrefill(item.start),
        ),
      });
    }
  }, [cycleConfigData, isUpdateCycleModalVisible]);

  const getCampaignCycleList = () => {
    const params = {
      incentive_campaign_id: +campaign,
    };
    dispatch(getCyclesForCampaignAsync(params));
  };

  const updateCycle = () => {
    openUpdateCycleModal(true);
  };

  const downloadCycleDetails = async (item: CycleType) => {
    const params = {
      slug: `${type}-commission-data-download-report`,
      report_params: {
        campaign_cycle_id: item.id,
      },
    };
    const iframeUrlResponse = await dispatch(openCycleIframeAsync(params));
    if (!('error' in iframeUrlResponse)) {
      openIframeUrl(true);
    }
  };

  const settleCycle = (item: CycleType) => {
    openSettleModal(true);
    setSettlementDetail(item);
  };

  const confirmCycleSettlement = async () => {
    const params = {
      campaign_cycle_id: +cycleId,
    };
    const settleCycleResponse = await dispatch(settleCycleAsync(params));
    if (!('error' in settleCycleResponse)) {
      openSettleModal(false);
      getCampaignCycleList();
    }
  };

