const InnerSubDynamicLogisticRoute = () => {
  const subhubname = SubPanelConstant();
  const { panel, innersubpanel } = GetRouterParams();
  const isAgentDispatchPanel =
    panel === AGENT_DISPATCH_PANEL && subhubname === 'hub' && innersubpanel;

  const isTransitPanel =
    panel === TRASNIT_DISPATCH_PANEL || panel === RETURN_TRANSIT_DISPATCH_PANEL;

  if (isTransitPanel) {
    return (
      <ShopUpLayout hideFooter={false}>
        <BulkTransitReceive />
      </ShopUpLayout>
    );
  }

  if (isAgentDispatchPanel)
    return (
      <ShopUpLayout hideFooter={false}>
        <DispatchToAgent />
      </ShopUpLayout>
    );

  const cmhReceivePanel =
    (subhubname === 'hub' || subhubname === 'returnHub') && innersubpanel;
  const rmhReceivePanel = subhubname === 'rmh' && innersubpanel;
  return (
    <ShopUpLayout hideFooter>
      {cmhReceivePanel && <CMHReceive />}
      {rmhReceivePanel && <RMHReceiveFromCMH />}
    </ShopUpLayout>
  );
};
